bTra = uniques(tra['B'])
aSim = uniques(sim['A'])
bSim = uniques(sim['B'])

Tra = uniques(tra['A'] + tra['B'])
Sim = uniques(sim['A'] + sim['B'])

def relation(a,b):
    d = {}
    for i in range(len(a)):
        d[a[i]] = b[i]
    return d

a = relation(aSim, aTra)
b = relation(bSim, bTra)

ab = relation(Sim, Tra)

print(str(ab).replace('\'','"'))
